mapper.select:
SELECT role_id
FROM auth_user_role
WHERE user_id = #{userId} AND tenant_id = #{tenantId}

SELECT user_id
FROM auth_user_role
WHERE role_id = #{roleId} AND tenant_id = #{tenantId}

SELECT COUNT(1) > 0
FROM auth_user_role
WHERE user_id = #{userId} AND role_id = #{roleId} AND tenant_id = #{tenantId}
